import javax.servlet.http.*;
import java.util.HashSet;

public class SecureServlet extends HttpServlet {
    // 定义允许反序列化的类白名单
    private static final HashSet<String> ALLOWED_CLASSES = new HashSet<>();
    static {
        ALLOWED_CLASSES.add("com.example.SafeData");
        ALLOWED_CLASSES.add("java.time.LocalDate");
        // 仅添加业务需要的安全类
    }

    protected void doPost(HttpServletRequest request, HttpServletResponse response) {
        try {
            // 使用验证输入流的ObjectInputStream
            SecureObjectInputStream ois = new SecureObjectInputStream(
                request.getInputStream(),
                ALLOWED_CLASSES
            );

            // 安全反序列化（仅允许白名单中的类）
            Object obj = ois.readObject();

            // 处理反序列化对象...
            processObject(obj);

        } catch (InvalidClassException e) {
            // 记录安全事件并拒绝请求
            logSecurityEvent("尝试反序列化未授权类: " + e.getMessage());
            response.setStatus(HttpServletResponse.SC_BAD_REQUEST);
        } catch (Exception e) {
            // 通用错误处理
            response.setStatus(HttpServletResponse.SC_INTERNAL_SERVER_ERROR);
        }
    }

    // 自定义安全ObjectInputStream
    private static class SecureObjectInputStream extends ObjectInputStream {
        private final HashSet<String> allowedClasses;

        public SecureObjectInputStream(InputStream in, HashSet<String> allowedClasses) 
                throws IOException {
            super(in);
            this.allowedClasses = allowedClasses;
        }

        @Override
        protected Class<?> resolveClass(ObjectStreamClass desc) 
                throws IOException, ClassNotFoundException {
            String className = desc.getName();

            // 验证类是否在白名单中
            if (!allowedClasses.contains(className)) {
                throw new InvalidClassException(
                    "未授权的反序列化尝试: " + className
                );
            }

            return super.resolveClass(desc);
        }
    }

    private void processObject(Object obj) {
        // 业务逻辑
    }

    private void logSecurityEvent(String message) {
        // 实现安全事件日志记录
    }
}
