A = (0,0);
B = (7,2);
C = (1,3);
D = B + C;

draw(A--B,Arrow(6));
draw(A--C,Arrow(6));
draw(B--C);
draw(B--D--C,dashed);

label("$O$", A, SW);
label("$A$", B, SE);
label("$B$", C, W);
[/asy]

